import React,{useState} from 'react'
import './Form.scss'
const Form = () => {
	var [emptyInput,setEmptyInput]=useState()
	function sendLink(e){
		e.preventDefault()
		var link=e.target.value
		fetch(`https://rel.ink/api/links/{
  url: "https://www.google.com"
}`,{method:'POST',
    headers: 
    {
        "Content-Type": "application/x-www-form-urlencoded"
    },

}).then(a=>a.json())
	}
	return (
		<form onSubmit={(e)=>sendLink(e)}>
			<input className={emptyInput===""?"error":""} onBlur={(e)=>setEmptyInput(e.target.value)} type="text" placeholder="Shorten a link here..."/>
			{emptyInput===""?<span>Please add a link</span>:""}
			<button>Shorten It!</button>
		</form>
	)
}

export default Form
