import React,{useState} from 'react'
import './Form.scss'
const Form = () => {
	var [emptyInput,setEmptyInput]=useState()
	function checkLink(e){
		if(e.target.value=""){
			setEmptyInput(true)
		}else{
			setEmptyInput(false)
		}
	}
	return (
		<form>
			<input onBlur={(e)=>checkLink(e)} type="text" placeholder="Shorten a link here..."/>
			{emptyInput?"":"aaaa"}
			<button>Shorten It!</button>
		</form>
	)
}

export default Form
