import React,{useState} from 'react'
import './Form.scss'
const Form = () => {
	var [emptyInput,setEmptyInput]=useState()
	async function sendLink(e){
		e.preventDefault()
		var link=()=>{
			var regex=new RegExp(`/\b(https?:\/\/.*?\.[a-z]{2,4}\/[^\s]*\b)/g`,'gi')
			if(!regex.match(e.target.value)){
				setEmptyInput('')
				return 
			}else{
				return e.target.value
			}
		}
		var value=await fetch(`https://rel.ink/api/links/?url=${link}`,{method:'POST',
    headers: 
    {
        "Content-Type": "application/json"
    },
    body:JSON.stringify({url:link})

})
		var resData=await value.json()
		console.log(resData)
	}
	return (
		<form onSubmit={(e)=>sendLink(e)}>
			<input className={emptyInput===""?"error":""} onBlur={(e)=>setEmptyInput(e.target.value)} type="text" placeholder="Shorten a link here..."/>
			{emptyInput===""?<span>Please add a link</span>:""}
			<button>Shorten It!</button>
		</form>
	)
}

export default Form
